var TempArr = [];

function bodyevent(n, o, i) {
    $(n + " option").each(function(e, r) { TempArr[e] = $(this).text() });
    $(document).bind("click", function(e) {
        var e = e || window.event;
        var r = e.target || e.srcElement;
        while (r) {
            if (r.id && (r.id == n.replace("#", "") || r.id == o.replace("#", "") || r.id == i.replace("#", ""))) { return }
            r = r.parentNode
        }
        $(n).css({ display: "none", "border-color": "#b8b8b8" });
        $(o).css({ "border-color": "#b8b8b8" });
        $(i + " span").removeClass("up")
    })
}

function changeF(e, r, n, o) {
    $(e).prevAll("input[id='" + n + "']").val($(e).find("option:selected").text());
    $(r).css({ display: "none" });
    $(o + " span").removeClass("up")
}

function setfocus(e, r, n, o) {
    $(r).css({ display: "", "border-color": "#38f" });
    $(n).css({ "border-color": "#38f" });
    $(o + " span").addClass("up");
    var t = $(r);
    t.html("");
    for (i = 0; i < TempArr.length; i++) {
        var p = $("<option></option>").text(TempArr[i]);
        t.append(p)
    }
}

function setinput(e, r) {
    var n = $(r);
    n.html("");
    for (i = 0; i < TempArr.length; i++) {
        if (TempArr[i].indexOf(e.value) >= 0) {
            var o = $("<option></option>").text(TempArr[i]);
            n.append(o)
        }
    }
}